import React , {useState} from 'react';

import './ExpenseForm.css';

const ExpenseForm = () => {
    const [enteredTitle , setnewTitle] = useState('');

    const titleChanger = (e)=>{
        setnewTitle(e.target.value);
        
}


    const [enteredAmount , setAmount] = useState('');
    const amountChanger = (e)=>{
        setAmount(e.target.value);

}
    const [enteredDate , setEnteredDate] = useState('');
    const dateChanger = (e)=>{
        setEnteredDate(e.target.value);
    }
    
    const submitHandler = (e)=>{
        e.preventDefault()

        const newEnteredData ={
            title:enteredTitle,
            amount:enteredAmount,
            date : new Date(enteredDate)
        }

        console.log(newEnteredData);
        setnewTitle('');
        setAmount('');
        setEnteredDate('');

    }

  return (
    <form onSubmit={submitHandler}>
      <div className='new-expense__controls'>
        <div className='new-expense__control'>
          <label>Title</label>
          <input type='text' value={enteredTitle} onChange={titleChanger} />
        </div>
        <div className='new-expense__control'>
          <label>Amount</label>
          <input type='number' value={enteredAmount} onChange={amountChanger} min='0.01' step='0.01' />
        </div>
        <div className='new-expense__control'>
          <label>Date</label>
          <input type='date' value={enteredDate} min='2019-01-01' max='2022-12-31' onChange={dateChanger} />
        </div>
      </div>
      <div className='new-expense__actions'>
        <button type='submit'>Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
